function Tab(t){const a=document.querySelector(t),e=a.querySelectorAll(".tabs__btn"),s=a.querySelectorAll(".tabs__content");a.addEventListener("click",(function(t){if(t.target.classList.contains("tabs__btn")){const n=t.target.dataset.tabsPath;e.forEach(t=>{t.classList.remove("tabs__btn_active")}),document.querySelector(`[data-tabs-path="${n}"]`).classList.add("tabs__btn_active"),c=n,s.forEach(t=>{t.classList.remove("tabs__content_active")}),a.querySelector(`[data-tabs-target="${c}"]`).classList.add("tabs__content_active")}var c}))}